mapper.select:
/*kr.happyjob.study.adm.dao.SurveyDao.surveyTeacherList*/ select loginID , name , user_hp , user_email , SUBSTRING(user_regdate,1,10) as user_regdate from tb_userinfo
and name LIKE CONCAT('%', #{search}, '%')
and user_type = 'b'
ORDER BY loginID DESC LIMIT #{startPage}, #{pageSize}

select dc.detail_name as lecture_name , ui.name , lc.lecture_start , lc.lecture_end , lc.lecture_seq from tb_lecture lc inner join tb_userinfo ui ON ui.loginID = lc.loginID inner join tb_detail_code dc on dc.group_code = 'lecture_no' and dc.detail_code = lc.lecture_no
and lc.lecture_name LIKE CONCAT('%', #{lsearch}, '%')
and lc.loginID = #{loginID}
ORDER BY lecture_name asc LIMIT #{startPage}, #{pageSize}
