mapper.select:
select tq.qna_no, tq.qna_title, tq.qna_date, tu.loginID, tq.qna_count, tq.qna_content, tu.name from tb_qna tq inner join tb_userinfo tu ON tu.loginID = tq.loginID
and tu.name LIKE CONCAT("%", #{qnasearch}, "%")
ORDER BY tq.qna_no DESC LIMIT #{startnum}, #{pageSize}

mapper.insert:

	select IFNULL(MAX(qna_no+1),1)
	from tb_qna

insert tb_qna
(
	  qna_no
	, loginID
	, qna_date
	, qna_title
	, qna_content
	, qna_count
) values (
	  #{qna_no}
	, #{loginID}
	, #{qna_new_date}
	, #{qna_new_title}
	, #{qna_new_content}
	, 0
)

mapper.update:
		update tb_qna
  		   set qna_count = qna_count + 1
  		 where qna_no = #{qnano}

		update tb_qna
   		   set qna_title = #{qna_new_title}
             , qna_content = #{qna_new_content}
 		 where qna_no = #{qna_no}
